mapper.select:
SELECT a.ui_uuid, a.ui_username, a.ui_loginname, a.ui_attribute, a.ui_status, a.ui_create_man, a.ui_create_time, a.identity_code, a.area_code, b.uei_special_mark, b.uei_type FROM ( select * from user_info where ui_loginname = #{loginName,jdbcType=VARCHAR}
and identity_code = #{identityCode,jdbcType=VARCHAR}
) a LEFT JOIN user_extend_info b ON a.ui_uuid = b.ui_uuid

SELECT a.ui_uuid, a.ui_username, a.ui_loginname, a.ui_attribute, a.ui_status, a.ui_create_man, a.ui_create_time, a.identity_code, a.area_code, b.uei_special_mark, b.uei_type, a.ui_password FROM ( select * from user_info where ui_username LIKE CONCAT('%','${userName}','%' ) and ui_status = 'normal'
and ui_create_man = #{createMan,jdbcType=VARCHAR}
ORDER BY ui_create_time desc ) a LEFT JOIN user_extend_info b ON a.ui_uuid = b.ui_uuid order by a.ui_create_time desc

select DISTINCT(uei_special_mark) from user_extend_info
where ui_uuid in (
    select ui_uuid from user_role_info
    where ri_uuid in (
        select DISTINCT(ri_uuid) from authority
        where ri_uuid in (
            select ri_uuid from authority
            where sr_uuid in (
                select us_uuid from use_scope where us_code = #{usCode,jdbcType=VARCHAR}
            )
            AND a_operate_level = 'show'
        ) and sr_uuid = (
            select sr_uuid from service_resource where sr_name = #{srName,jdbcType=VARCHAR}
        )
        AND  a_operate_level = 'show'
    )
) and uei_special_mark is not null

SELECT a.ui_uuid, a.ui_username, a.ui_loginname, a.ui_attribute, a.ui_status, a.ui_create_man, a.ui_create_time, a.identity_code, a.area_code, b.uei_special_mark, b.uei_type, a.ui_password FROM ( select * from user_info where ui_username LIKE CONCAT('%','${userName}','%' ) and ui_status not in ('unaudited','deleted')
and ui_create_man = #{createMan,jdbcType=VARCHAR}
ORDER BY ui_create_time desc ) a LEFT JOIN user_extend_info b ON a.ui_uuid = b.ui_uuid ORDER BY a.ui_create_time DESC
